graphql.query:
query GetAdvancedReport {
  advancedCompliance {
    report(input: {
      symbol: "AMD",
      methodology: AAOIFI
    }) {
      symbol
      rawSymbol
      name
      figi
      exchange
      status
      reportDate
      businessScreen
      financialScreen
      compliantRevenue
      nonCompliantRevenue
      questionableRevenue
      ... on AAOIFIReport {
        securitiesToMarketCapRatio 
        debtToMarketCapRatio 
      }
    }
  }
}
